        "[english]DOTA_Tooltip_ability_tiny_grow_bonus_damage": "BONUS DAMAGE:",
        "DOTA_Tooltip_ability_tiny_grow_bonus_attack_speed": "ANGRIFFSTEMPO-VERLUST:",
        "[english]DOTA_Tooltip_ability_tiny_grow_bonus_attack_speed": "ATTACK SPEED LOSS:",
        "DOTA_Tooltip_ability_tiny_grow_bonus_movement_speed": "BEWEGUNGSTEMPO:",
        "[english]DOTA_Tooltip_ability_tiny_grow_bonus_movement_speed": "MOVEMENT SPEED:",
